def analyse(alpha,depth,text):
    n_alpha = []
    n_proba = []
    first = []
    first_proba = []
    for i in range(len(text)-depth-1):
        b = text[i:i+depth]
        c = text[i+depth]
        if " " not in b:
            if b not in n_alpha:
                n_alpha.append(b)
                n_proba.append([0 for i in alpha])
            n_proba[n_alpha.index(b)][alpha.index(c)] +=1
        elif b[0]==" " and (" " not in b[1:]) and c != " ":
            d = b+c
            if d not in first:
                first.append(d)
                first_proba.append(0)
            first_proba[first.index(d)] +=1
    n_proba = [[x/sum(i) for x in i]for i in n_proba]
    first_proba = [x/sum(first_proba) for x in first_proba]
    return n_alpha,n_proba,first,first_proba

def create(depth,words_number,language):
    languages = {'e':eng,'f':fr, 'ff':fr2}
    text = languages[language]
    alpha = sorted(set(text))
    n_alpha, n_proba, first, first_proba = analyse(alpha,depth,text)
    words = []
    while len(words) < words_number:
        word = choice(first,p=first_proba)
        while True:
            if word[-1] == " ":
                if word[:-1] not in text:
                    words.append(word[:-1])
                    break
                break
            word += choice(alpha, p=n_proba[n_alpha.index(word[-depth:])])
    return '\n'.join(words)

print(create(depth,words_number,language))
